import React from 'react';
import { getAnswers, string2currency} from '../utils';
import '../App.css';
import CurrencyFormat from 'react-currency-format';

class HomeScreen extends React.Component{

    constructor(props){
        const predefinedAnswers = getAnswers(props.match.params);
        state = {
            channels: [],
            carTypes: [],
            fuelTypes: [],
            channel: predefinedAnswers[0],
            carType: predefinedAnswers[1],
            fuelType: predefinedAnswers[2],
            totCost:predefinedAnswers[3],
            downPayment:predefinedAnswers[4],
            term:predefinedAnswers[5]
        }
    }
    componentDidMount(){
        fetch("http://localhost:8080/car-finance")
        .then(response => response.json())
        .then(data => {
            this.setState({
                channels: data.channels,
                carTypes: data.carTypes,
                fuelTypes: data.fuelTypes,
                channel: data.channels[0],
                carType: data.carTypes[0],
                fuelType: data.fuelTypes[0],
            })
        });
    }

    handleSubmit(event){
        this.props.history.push('displayFlights/'+this.from+'/'+this.to+'/'+this.departureDate)
    }

    radioChange(event) {
        console.log(event.target.name, event.target.value)
        this.setState({
            [event.target.name]: [event.target.value]
        })
    }
    getRadioEntry = (key, li, placeholder, setter) => {
        return <div> <span> {key}: </span> {li.map((item, i) =>
                <label key = {item}>
            <input type="radio"
                key = {item}
                checked={placeholder == item}
                onChange={setter}
            /> {item} </label>)}
            </div>
    }

    getTextInput = (header, placeholder, setter, unit) => {
        return <div><span className = "header"> {header}: </span>
        <CurrencyFormat format="### ### ###"
        type ="text" name = {header}
        value={placeholder}
        onChange={
            (event) => setter((event.target.value.replaceAll(' ', '')))
        }
        /> <label>{unit}</label>
        </div>
    }

    render(){
        return <div>

            <h2 className = "banner">Welcome To Car Finance</h2>
            <form>
                {this.getRadioEntry("Channel", this.state.channels, this.state.channel)}
                {this.getRadioEntry("Car Type", this.state.carTypes, this.state.fuelTypes)}
                {this.getRadioEntry("Fuel Type", this.state.fuelTypes, this.state.fuelTypes)}
                {this.getTextInput("Car Price", this.state.totCost, (value) => this.setState({totCost: value}), "kr")}
                {this.getTextInput("Down Payment", this.state.downPayment, (value) => this.setState({downPayment: value}, "kr"))}
                {this.getTextInput("Loan Term", this.state.term, (value) => this.setState({term: value}), "years")}
                <button onClick={this.handleSubmit.bind(this)}>Search</button>
            </form>
        </div>
    }
}

export default HomeScreen;
