import React from 'react';
import {Link} from 'react-router-dom';
import {string2currency,  getAnswers, getHeaders} from '../utils';
import '../App.css';
class ProductScreen extends React.Component{
    constructor(props){
        state ={
            offers:[]
        }
    }

    getQuery(){
        const answers = getAnswers(this.props.match.params);
        const path = getHeaders().map((item, i) => 
            (item, answers).join('=')
        ).join('&');
        console.log(path)
        return path
    }

    componentDidMount(){
        fetch('http://localhost:8080/car-finance/get-offers?'+ this.getQuery())
        .then(response => response.json())
        .then(data => {
            var offers = [];
            if (data.offers != undefined) offers = data.offers;
            this.setState({
                offers: offers,
                message: data.message
            })
        });
    }

    formatInterestRate =(rate) => {
        return rate.toFixed(2) * 100;
    }

    getCard = (item) => {
        return <div>
            <button className="card">
            <h2 className = "header"> Product Name: {item.name}</h2>
            <p> Annual Payment: {item.annualPayment}</p>
            <p className="small">Note: To alter your parameters or to revisit your choices, please click on the "Go Back" button to the upper left. To start over, please click on the "Start Over" button further down.</p>
            <ul>
            <li> Car Price: {item.totCost} </li>
            <li> Down Payment: {item.totCost} </li>
            <li> Interest Rate {item.interestRate}</li>
            <ul> 
               <li> Base interest rate {this.formatInterestRate(Number.parseFloat(item.baseInterestRate))} % </li>
               <li> Adjustment due to car or fuel type: {this.formatInterestRate(Number.parseFloat(item.interestRate) - Number.parseFloat(item.baseInterestRate))} % </li>
            </ul>
            </ul>
            </button>
        </div>
    }
    render(){
        return <div>
            <h2 className = "banner">
            <Link to={'/'+this.getAnswers(this.props.match.params).join("/")} style={{ textDecoration: 'none' }}>
                    {"< Go Back"}</Link></h2>
            <h2 class="header">Message From the Bank</h2>
            <p style={{textAlign:"left"}}>{this.state.message}</p>
            {this.state.offers.map(item => this.getCard(item))}
            <button className ="card">
            <Link to={'/'+this.getAnswers(this.props.match.params).join("/")} style={{ textDecoration: 'none' }}>
            <h2>Start Over</h2>
            </Link>
            </button>
        </div>
    }
}


export default ProductScreen;
