import React from 'react';
import { getAnswers, getUserFriendlyHeaders, num2currency} from '../utils';
import '../App.css';
import NumberFormat from 'react-number-format';
class HomeScreen extends React.Component{

    constructor(props){
        super();
        const predefinedAnswers = getAnswers(props.match.params);
        this.state = {
            channels: [],
            carTypes: [],
            fuelTypes: [],
            channel: predefinedAnswers[0],
            carType: predefinedAnswers[1],
            fuelType: predefinedAnswers[2],
            totCost:predefinedAnswers[3],
            downPayment:predefinedAnswers[4],
            term:predefinedAnswers[5],
            warning: ""
        }
    }
    componentDidMount(){
        fetch("http://localhost:8080/car-finance")
        .then(response => response.json())
        .then(data => {
            this.setState({
                channels: data.channels,
                carTypes: data.carTypes,
                fuelTypes: data.fuelTypes,
                channel: this.prefillAnswers(this.state.channel, data.channels),
                carType: this.prefillAnswers(this.state.carType, data.carTypes),
                fuelType: this.prefillAnswers(this.state.fuelType, data.fuelTypes),
                //totCost: this.prefillAnswers(Number.parseInt(this.state.totCost), [100]),
                //downPayment: this.prefillAnswers =(this.state.totCost, ["10"]),
                //term:this.prefillAnswers =(this.state.totCost, ["10"]),
            })
            console.log(this.state);
        });


    }
    prefillAnswers =(prefilled, li)=>{
        return prefilled == undefined ? li[0] : prefilled;
    }
    handleSubmit = () => {
        const answersParsed = this.checkEmptyAnswers()
        if (answersParsed.length > 0){
            this.props.history.push('/products/'+answersParsed.join('/'));
        }
    }

    checkEmptyAnswers =()=>{
        var emptyAnswers = [];
        var headers  = getUserFriendlyHeaders();
        var answersParsed = []
        getAnswers(this.state).map((item, i)=>{
            if (item == undefined) emptyAnswers.push(headers[i]);
            else answersParsed.push(item);
        });
        if (emptyAnswers.length > 0){
            this.setState({
                warning: "Please provide the " + emptyAnswers.join(", ") + ". "
            });
            return [];
        } else return answersParsed;
    }


    getRadioEntry = (key, li, placeholder, setter) => {
        return <div> <span> {key}: </span> {li.map((item, i) =>
                <label key = {item}>
            <input type="radio"
                key = {item}
                name={key}
                value={item}
                checked = {placeholder == item}
                onChange={setter}

            /> {item.replaceAll('_','')} </label>)}
            </div>
    }

    getTextInput = (header, placeholder, setter, unit) => {
        return <div><span className = "header"> {header}: </span>
        <NumberFormat format="### ### ###"
        type ="text" name = {header}
        value={parseInt(placeholder, 10)}
        onChange={
            (event) =>{
            var value = parseInt(event.target.value.replaceAll(/[^0-9]/g, ""),0);
            setter(isNaN(value)? "0":value)}}
        /> <label>{unit}</label>
        </div>

    }

    render(){
        return <div>
            <h2 className = "banner">Welcome To Car Finance</h2>
            <button className = "card warning">{this.state.warning}</button>

            <form className = "form">

                {this.getRadioEntry("Channel", this.state.channels, this.state.channel, 
                (event)=> { this.setState({channel:event.target.value});})}
                {this.getRadioEntry("Car Type", this.state.carTypes, this.state.carType,
                (event)=> { this.setState({carType:event.target.value});})}
                {this.getRadioEntry("Fuel Type", this.state.fuelTypes, this.state.fuelType,
                (event)=> { this.setState({fuelType:event.target.value});})}
                {this.getTextInput("Car Price", this.state.totCost, (value) => {this.setState({totCost: value});}, "kr")}
                {this.getTextInput("Down Payment", this.state.downPayment, (value) => this.setState({downPayment: value}), "kr")}
                {this.getTextInput("Loan Term", this.state.term, (value) => this.setState({term: value}), "years")}
            </form>
            <button className = "card" onClick={this.handleSubmit.bind(this)}>Submit</button>

        </div>
    }
}

export default HomeScreen;
